INSERT INTO `wp_posts` VALUES (81,1,'2013-02-24 13:41:09','2013-02-24 10:11:09','','موشک','','inherit','open','open','','80-revision','','','2013-02-24 13:41:09','2013-02-24 10:11:09','',80,'http://nourbaran.ir/80-revision/',0,'revision','',0),(82,1,'2013-02-24 13:45:30','2013-02-24 10:15:30','<h2>دستور العمل استفاده و نکات ایمنی (فرفره)</h2>\r\n<ol>\r\n	<li>در فضای باز استفاده کنید.</li>\r\n	<li>فرفره را روی زمین صاف و مناسب قرار دهید.</li>\r\n	<li>راس فتیله را روشن و حداقل 5 متر از آن فاصبه بگیرید.</li>\r\n	<li>بمنظور جلوگیری از عدم حرکت فرفره از قراردادن آن روی زمین نامناسب مثل چمن ، شن و یا هرگونه زمین ناهموار خودداری فرمائید.<img class=\"alignleft size-full wp-image-214\" alt=\"ferfereh-model\" src=\"http://nourbaran.ir/media/ferfereh-model.jpg\" width=\"255\" height=\"255\" /></li>\r\n</ol>\r\n<h2>اخطار</h2>\r\n<ol>\r\n	<li>از نگهداری این محصول در خانه خودداری کنید.</li>\r\n	<li>از بکار بردن این محصولات جهت شوخی با دیگران اجتناب کنید.</li>\r\n	<li>در صورت عمل نکردن محصول بعد از روشن کردن فتیله ،جهت خنثی نمودن ، آن را بداخل آب بیاندازید.</li>\r\n</ol>\r\n<h2>روش نگهداری</h2>\r\n<ol>\r\n	<li>با احتیاط حمل و نقل شود.</li>\r\n	<li>از باز کردن و دستکاری کردن محصول جداً خودداری شود.</li>\r\n	<li>در جای خشک و خنک و خارج از منزل و دور از مواد قابل اشتعال نگهداری شود.</li>\r\n</ol>','فرفره','','publish','open','open','','ferfereh','','','2013-03-27 16:30:16','2013-03-27 13:00:16','',0,'http://nourbaran.ir/?p=82',0,'post','',0),(83,1,'2013-02-24 13:43:32','2013-02-24 10:13:32','','پروانه','','inherit','open','open','','82-revision','','','2013-02-24 13:43:32','2013-02-24 10:13:32','',82,'http://nourbaran.ir/82-revision/',0,'revision','',0),(21,1,'2013-02-23 21:19:55','2013-02-23 17:49:55','<h2>دستورالعمل استفاده و نکات ایمنی</h2>\r\n<ol>\r\n	<li>در فضای باز استفاده شود.</li>\r\n	<li>لوله پرتاب را روی زمین مسطح و سخت بصورت ثابت قرار دهید بطوریکه امکان واژگون شدن نداشته باشد.</li>\r\n	<li>ضمن عقب نگهداشتن سر و دستان خود , راس فتیله را به طرف بیرون خم نموده و ضمن روشن نمودن فتیله از آن فاصله بگیرید.<img class=\"alignleft size-full wp-image-191\" alt=\"Kahkeshan-2inch-taki-model\" src=\"http://nourbaran.ir/media/Kahkeshan-2inch-taki-model.jpg\" width=\"255\" height=\"255\" /></li>\r\n</ol>\r\n<h2>اخطار</h2>\r\n<ol>\r\n	<li>فاصله ایمنی و شعاع عملیاتی کهکشان از جایگاههای مراسم , مردم , پمپ بنزین , مناطق مسکونی , مناطق صنعتی , انبار ها , مزارع و کلیه مواد قابل اشتغال و هرگونه منطقه خطر 50 متر می باشد.</li>\r\n	<li>از بکار بردن محصول جهت شوخی با یکدیگر استفاده نکنید.</li>\r\n	<li>از روشن کردن کهکشان در دست جداً خودداری فرمائید.</li>\r\n</ol>\r\n<h2>روش نگهداری</h2>\r\n<ol>\r\n	<li>با احتیاط حمل و نقل شود.</li>\r\n	<li>از باز کردن و دستکاری کردن محصول جداً خودداری شود.</li>\r\n	<li>در جای خشک و خنک (برروی پالت) و فقط در انبار مخصوص مهمات به مدت حداکثر سه سال قابل نگهداری می باشد.</li>\r\n</ol>','کهکشان  2 اینچ تکی','','publish','open','open','','kahkeshan-2inch-taki','','','2013-03-27 16:31:35','2013-03-27 13:01:35','',0,'http://nourbaran.ir/?p=21',0,'post','',0),(42,1,'2013-02-24 02:34:01','2013-02-23 23:04:01','','کهکشان  3 اینچ','','inherit','open','open','','40-revision','','','2013-02-24 02:34:01','2013-02-23 23:04:01','',40,'http://nourbaran.ir/40-revision/',0,'revision','',0),(22,1,'2013-02-23 21:19:32','2013-02-23 17:49:32','<ol>\n	<li>با احتیاط حمل و نقل شود.</li>\n	<li>از باز کردن و دستکاری کردن محصول جداً خودداری شود.</li>\n	<li>\n3- در جای خشک و خنک (برروی پالت) و فقط در انبار مخصوص مهمات به مدت حداکثر سه سال قابل نگهداری می باشد.</li>\n</ol>','کهکشان  2 اینچ تکی','','inherit','open','open','','21-revision','','','2013-02-23 21:19:32','2013-02-23 17:49:32','',21,'http://nourbaran.ir/21-revision/',0,'revision','',0),(38,1,'2013-02-24 01:55:46','2013-02-23 22:25:46','','کهکشان  2 اینچ','','inherit','open','open','','23-revision-7','','','2013-02-24 01:55:46','2013-02-23 22:25:46','',23,'http://nourbaran.ir/23-revision-7/',0,'revision','',0),(23,1,'2013-02-23 21:23:41','2013-02-23 17:53:41','<h2>دستورالعمل استفاده و نکات ایمنی</h2>\r\nاین رده از محصولات ، جهت برگزاری جشن ها و اعیاد ملی و مذهبی ، با نظارت مستقیم نهاد های ذیربط و با دریافت مجوز های لازم استفاده می شود.\r\n<ol>\r\n	<li>ابتدا جعبه کهکشان را روی زمین صاف و مناسب بصورتی که امکان واژگون شدن نداشته باشد ، قرار دهید و اطراف جعبه را با بلوک یا کیسه شن یا خاک محصور نمائید.</li>\r\n	<li>پس از مستقر کردن جعبه ، درب را کاملاً از جعبه جدا نموده و فتیله را با احتیاط به طرف بیرون هدایت نمائید.</li>\r\n	<li>در موقع روشن نمودن فتیله سر و دستان را عقب نگه دارید.</li>\r\n	<li>پس از روشن نمودن فتیله بلافاصله از جعبه دور شده و حداقل 15 متر از آن فاصله بگیرید.</li>\r\n	<li>چنانچه پس از روشن نمودن فتیله به هر دلیلی کهکشان عمل نکرد ، از نزدیک شدن و دستکاری آن جداً خودداری نمائید و پس از 15 دقیقه جعبه را جهت خنثی شدن کامل بداخل آب بیاندازید.</li>\r\n	<li>استفاده از افلام حفاظت فردی (کلاه کاسکت موتور سواری ، لباس کار کتانی و دستکش چرمی مناسب) به هنگام عملیات الزامی است.</li>\r\n	<li>پس از خاتمه عملیات با احتیاط جعبه را بازدید و محیط را پاک سازی کنید.</li>\r\n	<li>اجرای عملیات نور افشانی می بایستی فقط توسط افراد آموزش دیده و مجرب انجام گیرد.<img class=\"alignleft size-full wp-image-194\" alt=\"Kahkeshan-2inch-model\" src=\"http://nourbaran.ir/media/Kahkeshan-2inch-model.jpg\" width=\"255\" height=\"255\" /></li>\r\n</ol>\r\n<h2>اخطار</h2>\r\n<ol>\r\n	<li>در فضای باز استفاده شود.</li>\r\n	<li>فاصله ایمنی و شعاع عملیاتی کهکشان از جایگاههای مراسم , مردم , پمپ بنزین , مناطق مسکونی , مناطق صنعتی , انبار ها , مزارع و کلیه مواد قابل اشتغال و هرگونه منطقه خطر 50 متر می باشد.</li>\r\n	<li>در زمان وزش باد شدید ، انجام عملیات نور افشانی ممنوع می باشد.</li>\r\n	<li>از دستکاری کهکشان جداً خودداری نمائید.</li>\r\n	<li>از بکار بردن این محصول جهت شوخی با یکدیگر خودداری نمائید.</li>\r\n	<li>الزامی است مسئولین اجرا قبل از شروع نورافشانی محوطه عملیات را بازرسی و کلیه نکات ایمنی ، فواصل حفاظتی و امنیتی را رعایت و از تردد افراد غیر مسئول جلوگیری نمایند؛ بدیهی است بروز هرگونه حوادث و خطرات احتمالی بعهده مسئول اجرا می باشد.</li>\r\n</ol>\r\n<h2>روش نگهداری</h2>\r\n<ol>\r\n	<li>با احتیاط حمل و نقل شود.</li>\r\n	<li>از باز کردن و دستکاری کردن محصول جداً خودداری شود.</li>\r\n	<li>در جای خشک و خنک (برروی پالت) و فقط در انبار مخصوص مهمات به مدت حداکثر سه سال قابل نگهداری می باشد.</li>\r\n</ol>','کهکشان  2 اینچ','','publish','open','open','','kahkeshan-2inch','','','2013-03-27 16:31:30','2013-03-27 13:01:30','',0,'http://nourbaran.ir/?p=23',0,'post','',0),(40,1,'2013-02-24 02:35:42','2013-02-23 23:05:42','<h2>دستور العمل استفاده و نکات ایمنی</h2>\r\nاین رده از محصولات ، جهت برگزاری جشن ها و اعیاد ملی و مذهبی ، با نظارت مستقیم نهاد های ذیربط و با دریافت مجوز های لازم استفاده می شود.\r\n<ol>\r\n	<li>ابتدا جعبه کهکشان را روی زمین صاف و مناسب بصورتی که امکان واژگون شدن نداشته باشد ، قرار دهید و اطراف جعبه را با بلوک یا کیسه شن یا خاک محصور نمائید.</li>\r\n	<li>پس از مستقر کردن جعبه ، درب را کاملاً از جعبه جدا نموده و فتیله را با احتیاط به طرف بیرون هدایت نمائید.</li>\r\n	<li>در موقع روشن نمودن فتیله سر و دستان را عقب نگه دارید.</li>\r\n	<li>پس از روشن نمودن فتیله بلافاصله از جعبه دور شده و حداقل 15 متر از آن فاصله بگیرید.</li>\r\n	<li>چنانچه پس از روشن نمودن فتیله به هر دلیلی کهکشان عمل نکرد ، از نزدیک شدن و دستکاری آن جداً خودداری نمائید و پس از 15 دقیقه جعبه را جهت خنثی شدن کامل بداخل آب بیاندازید.</li>\r\n	<li>استفاده از افلام حفاظت فردی (کلاه کاسکت موتور سواری ، لباس کار کتانی و دستکش چرمی مناسب) به هنگام عملیات الزامی است.</li>\r\n	<li>پس از خاتمه عملیات با احتیاط جعبه را بازدید و محیط را پاک سازی کنید.</li>\r\n	<li>اجرای عملیات نور افشانی می بایستی فقط توسط افراد آموزش دیده و مجرب انجام گیرد.<img class=\"alignleft size-full wp-image-196\" alt=\"Kahkeshan-3inch-model\" src=\"http://nourbaran.ir/media/Kahkeshan-3inch-model.jpg\" width=\"255\" height=\"255\" /></li>\r\n</ol>\r\n<h2>اخطار</h2>\r\n<ol>\r\n	<li>در فضای باز استفاده شود.</li>\r\n	<li>فاصله ایمنی و شعاع عملیاتی کهکشان از جایگاههای مراسم , مردم , پمپ بنزین , مناطق مسکونی , مناطق صنعتی , انبار ها , مزارع و کلیه مواد قابل اشتغال و هرگونه منطقه خطر 100 متر می باشد.</li>\r\n	<li>در زمان 